import React from 'react';

// Komponent otrzymuje listę produktów i funkcje obsługi jako props
function Produkty({ products, cartItems, onAddToCart, onRemoveFromCart }) {

  // Funkcja sprawdzająca, czy produkt jest w koszyku
  const isProductInCart = (productId) => {
    return cartItems.some(item => item.id === productId);
  };

  return (
    <div>
      <h2>Nasze Produkty</h2>
      {products.length === 0 ? (
        <p>Brak dostępnych produktów.</p>
      ) : (
        <ul>
          {products.map(produkt => (
            <li key={produkt.id}>
              {produkt.nazwa} - {produkt.cena.toFixed(2)} PLN {/* Formatowanie ceny */}
              {isProductInCart(produkt.id) ? (
                // Jeśli produkt jest w koszyku, pokaż przycisk Usuń
                <button onClick={() => onRemoveFromCart(produkt.id)} style={{ marginLeft: '10px' }}>
                  Usuń z koszyka
                </button>
              ) : (
                // Jeśli produktu nie ma w koszyku, pokaż przycisk Dodaj
                <button onClick={() => onAddToCart(produkt)} style={{ marginLeft: '10px' }}>
                  Dodaj do koszyka
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Produkty;
